public class ArraySample {
			public static void main(String[] args) {
			String input = "Raju",ninput="";
			char ch;
			for(int i=0;i<input.length();i++)
			{
				ch=input.charAt(i);
				ninput=ch+ninput;
			}
			System.out.println(ninput);
			}
	}

			
